      let root = roots.charAt(0).toUpperCase() + roots.slice(1);
      let rootIndex= notes.indexOf(root);
      let extentionIndex;
      if((rootIndex+9) > 11){
        extentionIndex = (rootIndex + 9) - notes.length;
      } else{
        extentionIndex = rootIndex+9;
      }

     return chord(roots,'dom11')+' '+ notes[extentionIndex];
    }
}



